import React, {useEffect, useState} from 'react';
import './App.css';
import List from "./components/List";
import {Phone} from "./model/phone";
import {ItemService} from "./services/item.service";

function App() {

    const[items, setItems] = useState<Phone[]>([]);

    const[hasNext, setHasNext] = useState(true);

    const[actualPage, setActualPage] = useState(0);

    const[searchValue, setSearchValue] = useState("");

    const[isValid, setIsValid] = useState(false);

    const getNextPage = () => {
        if (hasNext) {
            new ItemService().getPage({page: actualPage + 1}).then(res => {
                setItems((oldItems) => {
                    oldItems.push(...res.content);
                    return oldItems;
                });
                setHasNext(!res.last);
                setActualPage(res.number);
            })
        }
    }

    useEffect(() => {
        new ItemService().getPage({page: actualPage}).then(res => {
            setItems(res.content);
            setHasNext(!res.last);
            setActualPage(res.number);
        });
    }, [])

    return (
        <div>
            <h1>International Phone Number Categorize</h1>
            <input type="text" placeholder={"Country here"} value={searchValue} onChange={(e) => setSearchValue(e.target.value)}/>
            <select name="state" value={isValid?1:0} onChange={(e) => setIsValid(!!e.target.value)}>
                <option value={1}>
                    Valid
                </option>
                <option value={0}>
                    Invalid
                </option>
            </select>
            <div>
                <span> COUNTRY NAME </span>
                <span> COUNTRY CODE </span>
                <span> PHONE NUMBER </span>
                <span> STATE </span>
            </div>
            <List items={items}/>
            <button onClick={getNextPage}>
                Load More
            </button>
        </div>
    );
}
export default App;
