import React, {useEffect, useState} from 'react';
import './App.css';
import List from "./components/List";
import {Phone} from "./model/phone";
import {ItemService} from "./services/item.service";

function App() {
    const [items, setItems] = useState<Phone[]>([]);
    const [hasNext, setHasNext] = useState(true);
    const [actualPage, setActualPage] = useState(0);
    const [searchValue, setSearchValue] = useState("");
    const [isValid, setIsValid] = useState(false);

    const getAll = () => {
        if (hasNext) {
            new ItemService().getPage({page: actualPage + 1}).then(res => {
                setItems((oldItems) => {
                    oldItems.push(...res.content);
                    return oldItems;
                });
                setHasNext(!res.last);
                setActualPage(res.number);
            })
        }
    }
    const getByCountryAndState = () => {
        new ItemService().getByCountryAndState({country: searchValue, state: isValid, page: 0})
            .then(res => {
                setItems(res.content);
                setHasNext(!res.last);
                setActualPage(res.number);
            })
    }
    const getByCountryAndStateNextPage = () => {
        if (hasNext && searchValue) {
            new ItemService().getByCountryAndState({
                country: searchValue,
                state: isValid,
                page: actualPage + 1
            }).then(res => {
                setItems((oldItems) => {
                    oldItems.push(...res.content);
                    return oldItems;
                });
                setHasNext(!res.last);
                setActualPage(res.number);
            })
        }
    }



    useEffect(() => {
        getAll()
    }, [])

    return (
        <div>
            <h1>International Phone Number Categorize</h1>
            <input type="text" placeholder={"Country here"} value={searchValue}
                   onChange={(e) => setSearchValue(e.target.value)}/>
            <button onClick={getByCountryAndState}>
                Search
            </button>
            <select name="state" value={isValid ? "valid" : "invalid"} onChange={(e) => {
                setIsValid(e.target.value === "valid")
            }}>
                <option value={"valid"}>
                    Valid
                </option>
                <option value={"invalid"}>
                    Invalid
                </option>
            </select>
            <div>
                <span> COUNTRY NAME </span>
                <span> COUNTRY CODE </span>
                <span> PHONE NUMBER </span>
                <span> STATE </span>
            </div>
            <List items={items}/>
            <button onClick={searchValue?getByCountryAndStateNextPage: getNextPage}>
                Load More
            </button>
        </div>
    );
}

export default App;
